// 验证客户端传递过来的user是否可以保存到数据库中
// 1.验证用户名和密码是否为空
const { name, password } = user;
if (!name || !password) {
  ctx.body = {
    code: -1001,
    message: "用户名和密码不能为空",
  };
  return;
}
// 2.判断name是否在数据库中已经存在;
const findName = await userService.findUserByName(name);
if (findName.length) {
  ctx.body = {
    code: -1002,
    message: "用户名已经存在",
  };
  return;
}
